import { useEffect, useRef } from 'react'
import './AnimatedBackground.css'

const AnimatedBackground = () => {
  const canvasRef = useRef(null)

  useEffect(() => {
    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    let animationId
    let mouseX = 0
    let mouseY = 0

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth
      canvas.height = window.innerHeight
    }

    resizeCanvas()
    window.addEventListener('resize', resizeCanvas)

    // Track mouse movement
    const handleMouseMove = (e) => {
      mouseX = e.clientX
      mouseY = e.clientY
    }

    window.addEventListener('mousemove', handleMouseMove)

    // Enhanced Particle class
    class Particle {
      constructor(x, y, size, speedX, speedY, color) {
        this.x = x
        this.y = y
        this.size = size
        this.speedX = speedX
        this.speedY = speedY
        this.color = color
        this.opacity = Math.random() * 0.5 + 0.2
        this.originalSize = size
        this.pulseSpeed = Math.random() * 0.02 + 0.01
        this.pulseOffset = Math.random() * Math.PI * 2
      }

      update() {
        this.x += this.speedX
        this.y += this.speedY

        // Mouse interaction
        const dx = mouseX - this.x
        const dy = mouseY - this.y
        const distance = Math.sqrt(dx * dx + dy * dy)
        
        if (distance < 100) {
          const force = (100 - distance) / 100
          this.x -= dx * force * 0.01
          this.y -= dy * force * 0.01
        }

        // Bounce off edges with some randomness
        if (this.x > canvas.width || this.x < 0) {
          this.speedX = -this.speedX * (0.8 + Math.random() * 0.4)
        }
        if (this.y > canvas.height || this.y < 0) {
          this.speedY = -this.speedY * (0.8 + Math.random() * 0.4)
        }

        // Keep particles within bounds
        this.x = Math.max(0, Math.min(canvas.width, this.x))
        this.y = Math.max(0, Math.min(canvas.height, this.y))

        // Pulsing effect
        this.size = this.originalSize + Math.sin(Date.now() * this.pulseSpeed + this.pulseOffset) * 2
      }

      draw() {
        ctx.save()
        ctx.globalAlpha = this.opacity
        ctx.fillStyle = this.color
        
        // Create gradient effect
        const gradient = ctx.createRadialGradient(this.x, this.y, 0, this.x, this.y, this.size)
        gradient.addColorStop(0, this.color)
        gradient.addColorStop(1, this.color.replace('0.6', '0.1'))
        
        ctx.fillStyle = gradient
        ctx.beginPath()
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2)
        ctx.fill()
        ctx.restore()
      }
    }

    // Create particles
    const particles = []
    const particleCount = Math.min(80, Math.floor(window.innerWidth / 15))

    for (let i = 0; i < particleCount; i++) {
      const size = Math.random() * 4 + 2
      const x = Math.random() * canvas.width
      const y = Math.random() * canvas.height
      const speedX = (Math.random() - 0.5) * 0.8
      const speedY = (Math.random() - 0.5) * 0.8
      const colors = [
        'rgba(102, 126, 234, 0.6)', // Primary
        'rgba(240, 147, 251, 0.6)', // Accent
        'rgba(118, 75, 162, 0.6)',  // Primary-alt
        'rgba(72, 187, 120, 0.6)',  // Success
        'rgba(237, 137, 54, 0.6)',  // Warning
      ]
      const color = colors[Math.floor(Math.random() * colors.length)]

      particles.push(new Particle(x, y, size, speedX, speedY, color))
    }

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height)

      // Create subtle gradient background
      const gradient = ctx.createLinearGradient(0, 0, canvas.width, canvas.height)
      gradient.addColorStop(0, 'rgba(248, 244, 255, 0.1)')
      gradient.addColorStop(1, 'rgba(45, 55, 72, 0.1)')
      ctx.fillStyle = gradient
      ctx.fillRect(0, 0, canvas.width, canvas.height)

      // Update and draw particles
      particles.forEach(particle => {
        particle.update()
        particle.draw()
      })

      // Draw enhanced connections between nearby particles
      particles.forEach((particle, index) => {
        for (let j = index + 1; j < particles.length; j++) {
          const dx = particle.x - particles[j].x
          const dy = particle.y - particles[j].y
          const distance = Math.sqrt(dx * dx + dy * dy)

          if (distance < 120) {
            ctx.save()
            const opacity = (120 - distance) / 120 * 0.4
            ctx.globalAlpha = opacity
            ctx.strokeStyle = `rgba(102, 126, 234, ${opacity})`
            ctx.lineWidth = 1.5
            ctx.beginPath()
            ctx.moveTo(particle.x, particle.y)
            ctx.lineTo(particles[j].x, particles[j].y)
            ctx.stroke()
            ctx.restore()
          }
        }
      })

      // Draw mouse trail effect
      if (mouseX > 0 && mouseY > 0) {
        ctx.save()
        ctx.globalAlpha = 0.3
        ctx.fillStyle = 'rgba(240, 147, 251, 0.3)'
        ctx.beginPath()
        ctx.arc(mouseX, mouseY, 50, 0, Math.PI * 2)
        ctx.fill()
        ctx.restore()
      }

      animationId = requestAnimationFrame(animate)
    }

    animate()

    return () => {
      window.removeEventListener('resize', resizeCanvas)
      window.removeEventListener('mousemove', handleMouseMove)
      cancelAnimationFrame(animationId)
    }
  }, [])

  return (
    <div className="animated-background">
      <canvas ref={canvasRef} className="background-canvas" />
      <div className="floating-shapes">
        <div className="shape shape-1 morphing-shape"></div>
        <div className="shape shape-2 morphing-shape"></div>
        <div className="shape shape-3 morphing-shape"></div>
        <div className="shape shape-4 morphing-shape"></div>
        <div className="shape shape-5 morphing-shape"></div>
        <div className="shape shape-6 morphing-shape"></div>
      </div>
      <div className="gradient-overlay"></div>
    </div>
  )
}

export default AnimatedBackground
