import { useEffect, useRef } from 'react';
import { 
  initScrollAnimations, 
  initTextReveal, 
  initTypingAnimation, 
  initMorphingShapes, 
  initParticleSystem, 
  initAdvancedParallax, 
  initMagneticEffect, 
  initGlitchEffect, 
  init3DTiltEffect, 
  addFloatingElements, 
  initParallaxEffect, 
  addHoverEffects 
} from '../utils/animations';

export const useAnimations = () => {
  const initialized = useRef(false);

  useEffect(() => {
    if (initialized.current) return;
    
    const initAnimations = () => {
      // Initialize all advanced animations
      initScrollAnimations();
      initTextReveal();
      initTypingAnimation();
      initMorphingShapes();
      initParticleSystem();
      initAdvancedParallax();
      initMagneticEffect();
      initGlitchEffect();
      init3DTiltEffect();
      addFloatingElements();
      initParallaxEffect();
      addHoverEffects();

      initialized.current = true;
    };

    // Initialize with a delay to ensure React has rendered
    const timer = setTimeout(initAnimations, 300);

    return () => {
      clearTimeout(timer);
    };
  }, []);
};
